import React, { useState, useEffect, useRef } from 'react';
import './DonorDashboard.css';

function DonorDashboard() {
  const [activeTab, setActiveTab] = useState('categories');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [showDonationForm, setShowDonationForm] = useState(false);
  const [formStep, setFormStep] = useState(1);
  const [showItemDropdown, setShowItemDropdown] = useState(false);
  const [showConditionDropdown, setShowConditionDropdown] = useState(false);
  const [showTimeDropdown, setShowTimeDropdown] = useState(false);
  const itemDropdownRef = useRef(null);
  const conditionDropdownRef = useRef(null);
  const timeDropdownRef = useRef(null);
  
  // Donation form state
  const [formData, setFormData] = useState({
    category: '',
    itemType: '',
    quantity: '',
    condition: 'good',
    description: '',
    donorName: '',
    donorPhone: '',
    donorEmail: '',
    pickupLocation: '',
    pickupTime: '',
    pickupDate: '',
    additionalNotes: '',
    photoPreview: null,
  });

  // Sample data for demonstration
  const [donations, setDonations] = useState([]);
  const [activeDonation, setActiveDonation] = useState(null);

  // Load donations from localStorage on mount
  useEffect(() => {
    const storedDonations = JSON.parse(localStorage.getItem('donorDonations') || '[]');
    setDonations(storedDonations);
    // Set the most recent active donation
    const active = storedDonations.find(d => d.status !== 'delivered');
    if (active) setActiveDonation(active);
  }, []);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (itemDropdownRef.current && !itemDropdownRef.current.contains(event.target)) {
        setShowItemDropdown(false);
      }
      if (conditionDropdownRef.current && !conditionDropdownRef.current.contains(event.target)) {
        setShowConditionDropdown(false);
      }
      if (timeDropdownRef.current && !timeDropdownRef.current.contains(event.target)) {
        setShowTimeDropdown(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  // Condition options
  const conditionOptions = [
    { value: 'new', label: 'New (Unused)', icon: 'âœ¨', color: '#27ae60' },
    { value: 'excellent', label: 'Excellent', icon: 'ðŸ‘', color: '#3498db' },
    { value: 'good', label: 'Good', icon: 'ðŸ‘Œ', color: '#f39c12' },
    { value: 'fair', label: 'Fair', icon: 'ðŸ¤', color: '#e67e22' },
  ];

  // Time slot options
  const timeSlotOptions = [
    { value: '9am-12pm', label: '9:00 AM - 12:00 PM', icon: 'ðŸŒ…' },
